import React from "react";
import "./Product.css";

/* el title, price, etc... son props y se utilizan para cambiar valores */
/* ajustando desde home, en el apartado de product */
function Product({ title, price, image, rating }) {
  return (
    <div className="product">
      <div className="product__info">
        <p>{title}</p>
        {/* usa el props de title */}
        <p className="product__price">
          <small>$</small>
          <strong>{price}</strong>{/* usa el prop de precio */}
        </p>
        <div className="product__rating">
          <p>⭐</p>
          <p>⭐</p>
          <p>⭐</p>
        </div>
      </div>

      <img src="https://images-na.ssl-images-amazon.com/images/I/51Zymoq7UnL._AC_SY400_.jpg" />

      <button>Add to Basket</button>
    </div>
  );
}

export default Product;
