export const initialState = {
  basket: [],
  total: String,
};

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_BASKET":
      return {
        ...state,
        basket: [...state.basket, action.item],
        total: [...state.total, action.item]
      };
    default:
      return state;
  }
};

export default reducer;
